from aiohttp import webfrom handler import hula_socketimport socketioimport syssys.path.append('../../')from common import helpersio = socketio.AsyncServer(async_mode='aiohttp')app_room = web.Application()sio.attach(app_room)sio.register_namespace(hula_socket.HulaSocket('/room'))configRoom= helper.getServer('server')if __name__ == '__main__':    web.run_app(app_room,host=str(configRoom['host']),port=int(configRoom['port']))