class UserModel:    def __init__(self,pool):        self.pool=pool    async def getUserInfo(self,uid):        conn = await self.pool.acquire()        sql = "select * from user where uid = '%s' "        cur =  await conn.cursor()        await cur.execute(sql,(uid,))        userinfo=await cur.fetchone()        await cur.close()        conn.close()        return userinfo